import SortingVisualizer from './SortingVisualizer/SortingVisualizer';
import './App.css';
import './index.css';

import { ReactComponent as BellIcon } from './icons/bell.svg';
import { ReactComponent as MessengerIcon } from './icons/messenger.svg';
import { ReactComponent as CaretIcon } from './icons/caret.svg';
import { ReactComponent as PlusIcon } from './icons/plus.svg';
import { ReactComponent as CogIcon } from './icons/cog.svg';
import { ReactComponent as ChevronIcon } from './icons/chevron.svg';
import { ReactComponent as ArrowIcon } from './icons/arrow.svg';
import { ReactComponent as BoltIcon } from './icons/bolt.svg';
import { ReactComponent as CloudIcon } from './icons/sad-cloud.svg';
import { ReactComponent as TurtleIcon } from './icons/turtle.svg';


import React, { useState, useEffect, useRef } from 'react';
import { CSSTransition } from 'react-transition-group';

function App() {
  return (
    <div className="App">
      <Navbar>
        {/* <NavItem icon={<PlusIcon />} />
        <NavItem icon={<BellIcon />} />
        <NavItem icon={<MessengerIcon />} /> */}

        <NavItem icon={<CaretIcon />}>
          <DropdownMenu></DropdownMenu>
        </NavItem>
      </Navbar>
      <SortingVisualizer></SortingVisualizer>
    </div>
  );
}

function Navbar(props) {
  return (
    <nav className="navbar" style={{backgroundColor: "black"}}>
      <ul className="navbar-nav">{props.children}</ul>
    </nav>
  );
}

function NavItem(props) {
  const [open, setOpen] = useState(false);

  return (
    <li className="nav-item">
      <a href="#" className="icon-button" onClick={() => setOpen(!open)}>
        {props.icon}
      </a>

      {open && props.children}
    </li>
  );
}

function DropdownMenu() {
  const [activeMenu, setActiveMenu] = useState('main');
  const [menuHeight, setMenuHeight] = useState(null);
  const dropdownRef = useRef(null);

  useEffect(() => {
    setMenuHeight(dropdownRef.current?.firstChild.offsetHeight)
  }, [])

  function calcHeight(el) {
    const height = el.offsetHeight;
    setMenuHeight(height);
  }

  function DropdownItem(props) {
    return (
      <a href="#" className="menu-item" onClick={() => props.goToMenu && setActiveMenu(props.goToMenu)}>
        <span className="icon-button">{props.leftIcon}</span>
        {props.children}
        <span className="icon-right">{props.rightIcon}</span>
      </a>
    );
  }

  return (
    <div className="dropdown" style={{ height: menuHeight }} ref={dropdownRef}>

      <CSSTransition
        in={activeMenu === 'main'}
        timeout={500}
        classNames="menu-primary"
        unmountOnExit
        onEnter={calcHeight}>
        <div className="menu">
          <DropdownItem
            goToMenu="speed">
            Set Speed
          </DropdownItem>
          <DropdownItem goToMenu="color">
            Set Color
          </DropdownItem>

        </div>
      </CSSTransition>

      <CSSTransition
        in={activeMenu === 'speed'}
        timeout={500}
        classNames="menu-secondary"
        unmountOnExit
        onEnter={calcHeight}>
        <div className="menu">
          <DropdownItem goToMenu="main" leftIcon={<ArrowIcon />}>
            <h3>Set Speed</h3>
          </DropdownItem>
          <DropdownItem leftIcon={<BoltIcon/>}>Fast</DropdownItem>
          <DropdownItem leftIcon={<CloudIcon/>}>Medium</DropdownItem>
          <DropdownItem leftIcon={<TurtleIcon/>}>Slow</DropdownItem>
          {/* <DropdownItem>Awesome!</DropdownItem> */}
        </div>
      </CSSTransition>

      <CSSTransition
        in={activeMenu === 'color'}
        timeout={500}
        classNames="menu-secondary"
        unmountOnExit
        onEnter={calcHeight}>
        <div className="menu">
          <DropdownItem goToMenu="main" leftIcon={<ArrowIcon />}>
            <h3>Set Color</h3>
          </DropdownItem>
          <DropdownItem>Orange</DropdownItem>
          <DropdownItem>Grey</DropdownItem>
          <DropdownItem>turquoise</DropdownItem>
        </div>
      </CSSTransition>
    </div>
  );
}

export default App;
